mapper.select:
select A.*, B.username, B.nickname, B.avatar, C.title
from b_comment as A
         join b_user as B on (A.user_id=B.id)
         join b_thing as C on (A.thing_id=C.id)

    where A.thing_id=#{thingId}


    order by A.comment_time desc;


    order by A.like_count desc;

select A.*, B.username, C.title, C.cover
from b_comment as A
join b_user as B on (A.user_id=B.id)
join b_thing as C on (A.thing_id=C.id)

    where A.user_id=#{userId}

    order by A.comment_time desc;

SELECT A.id AS t_thing_id, A.title, A.cover, B.nickname, C.* FROM b_thing AS A JOIN b_user AS B ON B.id = A.user_id JOIN b_comment AS C ON C.thing_id = A.id JOIN b_user AS B2 ON B2.id = C.user_id
where B.id=#{userId}
order by C.comment_time desc;
